mapper.select:
SELECT COUNT(*) FROM T_BANNER_M01 WHERE DELETE_AT = 'N'
AND BANNER_NM LIKE CONCAT('%' , #{searchKeyword}, '%')
AND BANNER_TY = #{bannerTy}
AND USE_AT = #{useAt}
AND DATE_FORMAT(NOW(), '%Y-%m-%d') BETWEEN DATE_FORMAT(BANNER_BGNDE, '%Y-%m-%d') AND DATE_FORMAT(BANNER_ENDDE, '%Y-%m-%d')

mapper.update:
UPDATE T_BANNER_M01
SET UPDT_DE = NOW()
	,UPDT_ID = #{updtId}
	,BANNER_NM = #{bannerNm}
	,BANNER_DC = #{bannerDc}
	,BANNER_URL = #{bannerUrl}
	,ATCH_FILE_ID = #{atchFileId}
	,BANNER_BGNDE = #{bannerBgnde}
	,BANNER_ENDDE = #{bannerEndde}
	,BANNER_ORDR = #{bannerOrdr}
	,BANNER_TY = #{bannerTy}
	,USE_AT = #{useAt}
WHERE DELETE_AT = 'N'
  AND BANNER_SEQ_NO = #{bannerSeqNo}

UPDATE T_BANNER_M01
SET UPDT_DE = NOW()
	,UPDT_ID = #{updtId}
	,DELETE_AT = 'Y'
WHERE DELETE_AT = 'N'
  AND BANNER_SEQ_NO = #{bannerSeqNo}

UPDATE T_BANNER_M01
SET UPDT_DE = NOW()
	,UPDT_ID = #{updtId}
	,BANNER_ORDR = #{bannerOrdr}
WHERE DELETE_AT = 'N'
  AND BANNER_SEQ_NO = #{bannerSeqNo}

UPDATE T_BANNER_M01
SET UPDT_DE = NOW()
	,UPDT_ID = #{updtId}
	,USE_AT = #{useAt}
WHERE DELETE_AT = 'N'
  AND BANNER_SEQ_NO = #{bannerSeqNo}
